tinymce.PluginManager.add("insertimage", function(editor, url) {
	function showDialog(){
		editor.focus();
			
		var pluginWindow = editor.windowManager.open({
			title : "イメージを挿入",
			width : 320,
			height : 240,
			url : InsertImagePage
		});

		editor.windowManager.setParams({
			resize : function(w, h){
				pluginWindow.resizeTo(w, h);

				pluginWindow.width(w);
				pluginWindow.height(h);
			},
			width : 320,
			height : 240,
			plugin_url : url,
		});
	}
			
	editor.addButton("insertimage", {
		icon : "image",
		tooltip : "イメージを挿入",
		onclick : showDialog
//		image : url + '/img/icon.gif'
	});

	editor.addMenuItem("insertimage", {
		text : "イメージを挿入",
		onclick : showDialog,
		context : "挿入"
	});
});